// SurveyResponses.jsx
import React, { useState, useMemo } from 'react';
import { Card, Button, Accordion } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const EXCLUDED_FIELDS = new Set([
  'formhub/uuid', '_xform_id_string', '_uuid', '_attachments', 
  '_status', '_geolocation', '_tags', '_notes', '_validation_status',
  '_submitted_by', 'start', 'end', '__version__', 'meta/instanceID',
  '_submission_time'
]);

const BackButton = ({ onClick, text }) => (
  <Button 
    variant="link" 
    onClick={onClick}
    className="text-decoration-none p-0"
  >
    <ArrowLeft /> {text}
  </Button>
);

const SurveyResponses = ({ surveys, summary, onBack }) => {
  const [view, setView] = useState('summary');
  const [selectedDate, setSelectedDate] = useState(null);

  // Agrupa las respuestas por fecha usando _submission_time
  const groupedResponses = useMemo(() => {
    return surveys.reduce((acc, survey) => {
      const date = new Date(survey._submission_time).toLocaleDateString();
      (acc[date] = acc[date] || []).push(survey);
      return acc;
    }, {});
  }, [surveys]);

  // Construye un mapping de nombre de campo a etiqueta usando summary.name_quality.firsts
  const questionMapping = useMemo(() => {
    const mapping = {};
    if (summary && summary.name_quality && summary.name_quality.firsts) {
      const firsts = summary.name_quality.firsts;
      Object.keys(firsts).forEach(key => {
        const question = firsts[key];
        mapping[question.name] =
          question.label && question.label.length > 0 ? question.label[0] : question.name;
      });
    }
    return mapping;
  }, [summary]);

  // Función para formatear el nombre de la llave en caso de no contar con etiqueta
  const formatKey = (key) => key.replace(/_/g, ' ');

  // Función para renderizar el valor de la respuesta
  const renderValue = (value) => {
    if (value == null) return 'No respondido';
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }
    return Array.isArray(value) ? value.join(', ') : String(value);
  };

  const SummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de Respuestas</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {Object.entries(groupedResponses).map(([date, responses]) => (
            <div key={date} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{date}</h6>
                  <small className="text-muted">
                    {responses.length} respuesta{responses.length !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setSelectedDate(date);
                  }}
                >
                  Ver respuestas
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card.Body>
    </Card>
  );

  const DetailView = () => (
    <div>
      <BackButton 
        onClick={() => {
          setView('summary');
          setSelectedDate(null);
        }}
        text="Volver al resumen"
      />

      <div className="mb-3">
        <h5 className="d-flex align-items-center gap-2">
          Respuestas del {selectedDate}
          <span className="badge bg-primary">
            {groupedResponses[selectedDate]?.length || 0} respuestas
          </span>
        </h5>
      </div>

      <Accordion>
        {groupedResponses[selectedDate]?.map((response, index) => (
          <Accordion.Item key={index} eventKey={index.toString()}>
            <Accordion.Header>
              <div className="d-flex justify-content-between align-items-center w-100 me-3">
                <span>Respuesta #{index + 1}</span>
                <small className="text-muted">
                  {new Date(response._submission_time).toLocaleTimeString()}
                </small>
              </div>
            </Accordion.Header>
            <Accordion.Body className="bg-light">
              {Object.entries(response)
                .filter(([key]) => !EXCLUDED_FIELDS.has(key))
                .map(([key, value]) => (
                  <div key={key} className="mb-3 border-bottom pb-3">
                    <div className="fw-medium text-primary mb-2">
                      {/* Se muestra la etiqueta si existe en el mapping, o se formatea la llave */}
                      {questionMapping[key] || formatKey(key)}
                    </div>
                    <div className="ps-3">
                      {renderValue(value)}
                    </div>
                  </div>
                ))}
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
    </div>
  );

  return (
    <div className="container py-4">
      <div className="mb-4">
        <BackButton onClick={onBack} text="Volver a encuestas" />
        <h4 className="mt-3">Respuestas de la Encuesta</h4>
      </div>
      {view === 'summary' ? <SummaryView /> : <DetailView />}
    </div>
  );
};

export default SurveyResponses;
