import React, { useState } from 'react';
import { Button } from 'react-bootstrap'; // Importa el componente Button de react-bootstrap

const SurveyList = ({ surveys = [], onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [surveysPerPage] = useState(5); // Número de encuestas por página

  // Verificar si surveys es un array y no está vacío
  if (!Array.isArray(surveys)) {
    console.error("Error: surveys no es un array.", surveys);
    return <div>No se encontraron encuestas.</div>;
  }

  if (surveys.length === 0) {
    return <div>No hay encuestas disponibles.</div>;
  }

  // Filtrar encuestas que tienen summary y labels
  const validSurveys = surveys.filter(
    (survey) => survey.summary && survey.summary.labels && survey.summary.labels.length > 0
  );

  if (validSurveys.length === 0) {
    return <div>No hay encuestas con preguntas disponibles.</div>;
  }

  // Lógica de paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = validSurveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(validSurveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Encuestas del proyecto</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">Encuesta #{index + 1}</h3>
                {survey.summary.labels.map((label, labelIndex) => (
                  <div key={labelIndex} className="mb-2">
                    <strong className="text-secondary">Pregunta {labelIndex + 1}:</strong>{' '}
                    <span className="text-dark">{label}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <Button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(validSurveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
