import React, { useState, useEffect } from 'react';
import { Card, Button, Accordion, Pagination } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const SurveyResponses = ({ surveys, onBack }) => {
  const [expandedResponse, setExpandedResponse] = useState(null);
  const [view, setView] = useState('summary');
  const [surveyPage, setSurveyPage] = useState(1);
  const [responsePage, setResponsePage] = useState(1);

  const responsesPerPage = 5;
  const surveysPerPage = 5;

  // Paginación de encuestas
  const handleSurveyPageChange = (page) => {
    setSurveyPage(page);
  };

  // Paginación de respuestas
  const handleResponsePageChange = (page) => {
    setResponsePage(page);
  };

  // Agrupa las respuestas por fecha
  const groupedResponses = surveys.reduce((groups, survey) => {
    const date = new Date(survey.date_created).toLocaleDateString();
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(survey);
    return groups;
  }, {});

  // Función para renderizar valores
  const renderValue = (value) => {
    if (value === null || value === undefined) {
      return 'No respondido';
    }
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }
    if (Array.isArray(value)) {
      return value.join(', ');
    }
    return String(value);
  };

  // Renderizamos la vista de resumen
  const renderSummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de Encuestas</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {surveys.slice((surveyPage - 1) * surveysPerPage, surveyPage * surveysPerPage).map((survey, index) => (
            <div key={survey.url} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{survey.name}</h6>
                  <small className="text-muted">
                    {survey.deployment__submission_count} respuesta{survey.deployment__submission_count !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setExpandedResponse(survey);
                  }}
                >
                  Ver respuestas
                </Button>
              </div>
            </div>
          ))}
        </div>

        {/* Paginación de encuestas */}
        <Pagination>
          {[...Array(Math.ceil(surveys.length / surveysPerPage))].map((_, index) => (
            <Pagination.Item
              key={index}
              active={surveyPage === index + 1}
              onClick={() => handleSurveyPageChange(index + 1)}
            >
              {index + 1}
            </Pagination.Item>
          ))}
        </Pagination>
      </Card.Body>
    </Card>
  );

  // Renderizamos la vista detallada de las respuestas
  const renderDetailView = () => {
    const questions = expandedResponse.summary.labels || []; // Las preguntas de la encuesta

    return (
      <div>
        <Button
          variant="link"
          className="text-decoration-none mb-3"
          onClick={() => {
            setView('summary');
            setExpandedResponse(null);
          }}
        >
          <ArrowLeft /> Volver al resumen
        </Button>

        <div className="mb-3">
          <h5>
            Respuestas de {expandedResponse.name}
            <span className="badge bg-primary ms-2">
              {groupedResponses[expandedResponse.url]?.length || 0} respuestas
            </span>
          </h5>
        </div>

        <Accordion>
          {questions
            .slice((responsePage - 1) * responsesPerPage, responsePage * responsesPerPage)
            .map((question, index) => (
              <Accordion.Item key={index} eventKey={index.toString()}>
                <Accordion.Header>
                  <span>{question}</span>
                </Accordion.Header>
                <Accordion.Body className="bg-light">
                  {/* Aquí mostramos las respuestas de la pregunta */}
                  {groupedResponses[expandedResponse.url]?.map((response, idx) => (
                    <div key={idx}>
                      <strong>Respuesta #{idx + 1}:</strong>
                      <div>{renderValue(response[question])}</div>
                    </div>
                  ))}
                </Accordion.Body>
              </Accordion.Item>
            ))}
        </Accordion>

        {/* Paginación de preguntas */}
        <Pagination>
          {[...Array(Math.ceil(questions.length / responsesPerPage))].map((_, index) => (
            <Pagination.Item
              key={index}
              active={responsePage === index + 1}
              onClick={() => handleResponsePageChange(index + 1)}
            >
              {index + 1}
            </Pagination.Item>
          ))}
        </Pagination>
      </div>
    );
  };

  return (
    <div className="container py-4">
      <div className="mb-4">
        <Button
          variant="link"
          onClick={onBack}
          className="text-decoration-none p-0"
        >
          <ArrowLeft /> Volver a proyectos
        </Button>
        <h4 className="mt-3">Respuestas de la Encuesta</h4>
      </div>

      {view === 'summary' ? renderSummaryView() : renderDetailView()}
    </div>
  );
};

export default SurveyResponses;
