import React, { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import SurveyList from './SurveyList';
import LoadingSpinner from './LoadingSpinner'; // Importa el componente LoadingSpinner
import { fetchProjects, fetchSurveys } from '../services/apiService'; // Importa el servicio

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar proyectos
  useEffect(() => {
    const loadProjects = async () => {
      try {
        const data = await fetchProjects();
        // Filtra proyectos que tengan nombre y descripción
        const filteredProjects = data.filter(
          (project) => project.name && project.settings?.description
        );
        setProjects(filteredProjects);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadProjects();
  }, []);

  // Cargar encuestas de un proyecto
  const handleFetchSurveys = async (projectId) => {
    try {
      setLoading(true);
      const data = await fetchSurveys(projectId);
      setSurveys(data);
      setSelectedProject(projectId);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (error) {
    return (
      <div className="container mt-4">
        <p>Error: {error}</p>
        <button onClick={() => window.location.reload()}>Reintentar</button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {loading ? (
        <LoadingSpinner /> // Muestra el spinner mientras se cargan los datos
      ) : (
        <>
          {!selectedProject ? (
            <ProjectList projects={projects} onProjectSelect={handleFetchSurveys} />
          ) : (
            <SurveyList
              surveys={surveys}
              onBack={() => setSelectedProject(null)}
            />
          )}
        </>
      )}
    </div>
  );
};

export default Projects;
