import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar proyectos
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/projects');
      if (!response.ok) {
        throw new Error('Error al obtener los proyectos');
      }
      const data = await response.json();
      console.log("Respuesta de la API:", data); // Depuración
      setProjects(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Cargar encuestas de un proyecto
  const fetchSurveys = async (projectId) => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/projects/${projectId}/responses`);
      if (!response.ok) {
        throw new Error('Error al obtener las encuestas');
      }
      const data = await response.json();
      setSurveys(data);
      setSelectedProject(projectId);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Cargando...</div>;
  }

  if (error) {
    return (
      <div>
        <p>Error: {error}</p>
        <button onClick={fetchProjects}>Reintentar</button>
      </div>
    );
  }

  return (
    <div>
      <h1>Proyectos de KoboToolbox</h1>

      {!selectedProject ? (
        // Lista de proyectos
        <div>
          <h2>Selecciona un proyecto:</h2>
          {projects.map(project => (
            <div key={project.uid} onClick={() => fetchSurveys(project.uid)}>
              <h3>{project.name}</h3>
              <p>Descripción: {project.description || 'Sin descripción'}</p>
              <button>Ver encuestas</button>
            </div>
          ))}
        </div>
      ) : (
        // Vista de encuestas
        <div>
          <button onClick={() => setSelectedProject(null)}>← Volver a proyectos</button>
          <h2>Encuestas del proyecto</h2>
          {surveys.map((survey, index) => (
            <div key={index}>
              <h3>Encuesta #{index + 1}</h3>
              {Object.entries(survey).map(([key, value]) => (
                <div key={key}>
                  <strong>{key}:</strong> {String(value)}
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Projects;
