import React from 'react';

const SurveyList = ({ surveys, onBack }) => {
  // Función para formatear la geolocalización
  const formatGeolocation = (geolocation) => {
    if (!geolocation || geolocation.length === 0) return 'No disponible';
    const [lat, lon] = geolocation;
    if (lat === null || lon === null) return 'No disponible';
    return `Lat: ${lat}, Lon: ${lon}`;
  };

  // Función para formatear objetos
  const formatObject = (obj) => {
    if (!obj || Object.keys(obj).length === 0) return 'Sin datos';
    return JSON.stringify(obj, null, 2); // Convertir a formato JSON legible
  };

  return (
    <div>
      <button className="btn btn-secondary mb-3" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2>Encuestas del proyecto</h2>
      {surveys.map((survey, index) => (
        <div key={index} className="card mb-3">
          <div className="card-body">
            <h3 className="card-title">Encuesta #{index + 1}</h3>
            {Object.entries(survey).map(([key, value]) => (
              <div key={key} className="mb-2">
                <strong>{key}:</strong>{' '}
                {key === '_geolocation'
                  ? formatGeolocation(value) // Formatear geolocalización
                  : key === '_validation_status' || key === '_attachments' || key === '_tags' || key === '_notes'
                  ? formatObject(value) // Formatear objetos y arrays
                  : String(value)}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default SurveyList;
