import React, { useState } from 'react';
import { Button } from 'react-bootstrap'; // Importa el componente Button de react-bootstrap

const SurveyList = ({ surveyStructure, submissions = [], onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [submissionsPerPage] = useState(5); // Número de submisiones por página

  // Verificar si submissions es un array y no está vacío
  if (!Array.isArray(submissions)) {
    console.error("Error: submissions no es un array.", submissions);
    return <div>No se encontraron submisiones.</div>;
  }

  if (submissions.length === 0) {
    return <div>No hay submisiones disponibles.</div>;
  }

  // Obtener las preguntas de la estructura de la encuesta
  const questions = surveyStructure?.summary?.labels || [];

  if (questions.length === 0) {
    return <div>No se encontraron preguntas en la estructura de la encuesta.</div>;
  }

  // Lógica de paginación
  const indexOfLastSubmission = currentPage * submissionsPerPage;
  const indexOfFirstSubmission = indexOfLastSubmission - submissionsPerPage;
  const currentSubmissions = submissions.slice(indexOfFirstSubmission, indexOfLastSubmission);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(submissions.length / submissionsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Submisiones del proyecto</h2>
      <div className="row">
        {currentSubmissions.map((submission, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">Submisión #{index + 1}</h3>
                {questions.map((question, questionIndex) => {
                  // Obtener la clave de la pregunta (asumiendo que las claves coinciden con el orden de las preguntas)
                  const questionKey = Object.keys(submission).find(
                    (key) => key.toLowerCase().includes(question.toLowerCase())
                  );

                  // Mostrar la pregunta y la respuesta
                  return (
                    <div key={questionIndex} className="mb-2">
                      <strong className="text-secondary">Pregunta {questionIndex + 1}:</strong>{' '}
                      <span className="text-dark">{question}</span>
                      <br />
                      <strong className="text-secondary">Respuesta:</strong>{' '}
                      <span className="text-dark">{submission[questionKey] || "Sin respuesta"}</span>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <Button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(submissions.length / submissionsPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
