import React, { useState } from 'react';
import { Button, Card, Container, Row, Col, ListGroup } from 'react-bootstrap';

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const surveysPerPage = 5;

  // Paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <Container className="mt-4">
      <Button variant="outline-secondary" className="mb-4" onClick={onBack}>
        ← Volver a proyectos
      </Button>
      <h2 className="mb-4 text-center text-primary">Encuestas del Proyecto</h2>
      <Row>
        {currentSurveys.map((survey, index) => (
          <Col md={6} key={index} className="mb-4">
            <Card className="shadow-sm">
              <Card.Body>
                <Card.Title className="text-primary">📋 Encuesta #{index + 1}</Card.Title>
                <ListGroup variant="flush">
                  <ListGroup.Item><strong>ID:</strong> {survey._id || "Desconocido"}</ListGroup.Item>
                  <ListGroup.Item><strong>Inicio:</strong> {survey.start || "No disponible"}</ListGroup.Item>
                  <ListGroup.Item><strong>Fin:</strong> {survey.end || "No disponible"}</ListGroup.Item>
                  <ListGroup.Item><strong>Estado:</strong> {survey._status || "Desconocido"}</ListGroup.Item>
                  <ListGroup.Item><strong>Servicio Utilizado:</strong> {survey["_Qu_servicio_ha_utilizado_de_Conecta"] || "No especificado"}</ListGroup.Item>
                </ListGroup>
                <hr />
                <h5 className="text-secondary">📝 Respuestas:</h5>
                <ListGroup variant="flush">
                  {Object.entries(survey)
                    .filter(([key]) => !key.startsWith("_") && !["start", "end"].includes(key)) // Filtra metadatos irrelevantes
                    .map(([question, answer], i) => (
                      <ListGroup.Item key={i}>
                        <strong className="text-dark">{question.replace(/_/g, " ")}:</strong> {answer || "No respondido"}
                      </ListGroup.Item>
                    ))}
                </ListGroup>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          ⬅️ Anterior
        </Button>
        <Button onClick={nextPage} disabled={currentPage === Math.ceil(surveys.length / surveysPerPage)}>
          Siguiente ➡️
        </Button>
      </div>
    </Container>
  );
};

export default SurveyList;
