import React, { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import SurveyList from './SurveyList';
import LoadingSpinner from './Spinner';
import { fetchProjects, fetchSurveys } from '../services/apiService';
import { Button, Pagination } from 'react-bootstrap'; // Importa Pagination de react-bootstrap

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [projectsPerPage] = useState(1); // Número de proyectos por página

  // Cargar proyectos
  useEffect(() => {
    const loadProjects = async () => {
      try {
        const data = await fetchProjects();
        const filteredProjects = data.filter(
          (project) => project.name && project.settings?.description
        );
        setProjects(filteredProjects);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadProjects();
  }, []);

  // Cargar encuestas de un proyecto
  const handleFetchSurveys = async (projectId) => {
    try {
      setLoading(true);
      const data = await fetchSurveys(projectId);
      setSurveys(data);
      setSelectedProject(projectId);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Lógica de paginación
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = projects.slice(indexOfFirstProject, indexOfLastProject);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(projects.length / projectsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Función para manejar el cambio de página
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  if (error) {
    return (
      <div className="container mt-4">
        <p>Error: {error}</p>
        <button onClick={() => window.location.reload()}>Reintentar</button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {loading ? (
        <LoadingSpinner />
      ) : (
        <>
          {!selectedProject ? (
            <>
              <ProjectList projects={currentProjects} onProjectSelect={handleFetchSurveys} />
              
              {/* Paginación */}
              <Pagination className="mt-4 justify-content-center">
                <Pagination.Prev 
                  disabled={currentPage === 1} 
                  onClick={prevPage} 
                />
                {[...Array(Math.ceil(projects.length / projectsPerPage))].map((_, index) => (
                  <Pagination.Item 
                    key={index + 1} 
                    active={index + 1 === currentPage} 
                    onClick={() => handlePageChange(index + 1)}
                  >
                    {index + 1}
                  </Pagination.Item>
                ))}
                <Pagination.Next 
                  disabled={currentPage === Math.ceil(projects.length / projectsPerPage)} 
                  onClick={nextPage} 
                />
              </Pagination>
            </>
          ) : (
            <SurveyList
              surveys={surveys}
              onBack={() => {
                setSelectedProject(null);
                setCurrentPage(1); // Reinicia la paginación al volver
              }}
            />
          )}
        </>
      )}
    </div>
  );
};

export default Projects;
