import React, { useState, useMemo } from 'react';
import { Card, Button, Accordion, Image } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const EXCLUDED_FIELDS = new Set([
  '_id', 'formhub/uuid', '_xform_id_string', '_uuid', '_attachments',
  '_status', '_geolocation', '_tags', '_notes', '_validation_status',
  '_submitted_by', 'start', 'end', '__version__', 'meta/instanceID',
  '_submission_time'
]);

const BackButton = ({ onClick, text }) => (
  <Button 
    variant="link" 
    onClick={onClick}
    className="text-decoration-none p-0"
  >
    <ArrowLeft /> {text}
  </Button>
);

const SurveyResponses = ({ surveys, onBack }) => {
  const [view, setView] = useState('summary');
  const [selectedDate, setSelectedDate] = useState(null);

  const groupedResponses = useMemo(() => {
    return surveys.reduce((acc, survey) => {
      const date = new Date(survey._submission_time).toLocaleDateString();
      (acc[date] = acc[date] || []).push(survey);
      return acc;
    }, {});
  }, [surveys]);

  const renderValue = (key, value, response) => {
    if (value == null) return 'No respondido';
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }

    if (typeof value === 'string' && (value.endsWith('.png') || value.endsWith('.jpg') || value.endsWith('.jpeg'))) {
      const attachment = response._attachments?.find(att => att.question_xpath === key);
      if (attachment) {
        return (
          <div className="mt-2">
            <Image 
              src={`http://localhost:5000/api/image-proxy?url=${encodeURIComponent(attachment.download_url)}`} 
              alt={key} 
              thumbnail
              style={{ maxWidth: '150px', maxHeight: '150px' }}
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <br />
            <a href={attachment.download_url} target="_blank" rel="noopener noreferrer">
              Ver imagen en tamaño completo
            </a>
          </div>
        );
      }
    }

    if (key === 'Ubicacion' && typeof value === 'string') {
      const [lat, lon] = value.split(' ').map(Number);
      if (!isNaN(lat) && !isNaN(lon)) {
        return (
          <MapContainer center={[lat, lon]} zoom={15} style={{ height: '200px', width: '20%', marginTop: '10px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={[lat, lon]}>
              <Popup>Ubicación del reporte</Popup>
            </Marker>
          </MapContainer>
        );
      }
    }
    
    return Array.isArray(value) ? value.join(', ') : String(value);
  };

  const SummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de reporte</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {Object.entries(groupedResponses).map(([date, responses]) => (
            <div key={date} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{date}</h6>
                  <small className="text-muted">
                    {responses.length} reporte{responses.length !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setSelectedDate(date);
                  }}
                >
                  Ver reporte
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card.Body>
    </Card>
  );

  const DetailView = () => (
    <div>
      <BackButton 
        onClick={() => {
          setView('summary');
          setSelectedDate(null);
        }}
        text="Volver al resumen"
      />

      <div className="mb-3">
        <h5 className="d-flex align-items-center gap-2">
          Respuestas del {selectedDate}
          <span className="badge bg-primary">
            {groupedResponses[selectedDate]?.length || 0} respuestas
          </span>
        </h5>
      </div>

      <Accordion>
        {groupedResponses[selectedDate]?.map((response, index) => {
          const submittedBy = response.inspector_responsable || "Sin nombre";
          return (
            <Accordion.Item key={index} eventKey={index.toString()}>
              <Accordion.Header>
                <div className="d-flex justify-content-between align-items-center w-100 me-3">
                  <span className="fw-bold" style={{ color: 'black' }}>{index + 1}. {submittedBy}</span>
                  <small className="text-muted">
                    {new Date(response._submission_time).toLocaleTimeString()}
                  </small>
                </div>
              </Accordion.Header>
              <Accordion.Body className="bg-light">
                {Object.entries(response)
                  .filter(([key]) => !EXCLUDED_FIELDS.has(key))
                  .map(([key, value]) => (
                    <div key={key} className="mb-3 border-bottom pb-3">
                      <div className="fw-medium text-primary mb-2">
                        {key.startsWith('_') ? key.slice(1) : key}
                      </div>
                      <div className="ps-3">
                        {renderValue(key, value, response)}
                      </div>
                    </div>
                  ))}
              </Accordion.Body>
            </Accordion.Item>
          );
        })}
      </Accordion>
    </div>
  );

  return (
    <div className="container py-4">
      <div className="mb-4">
        <BackButton onClick={onBack} text="Volver a reportes" />
        <h4 className="mt-3">Reporte Subestacion</h4>
      </div>
      {view === 'summary' ? <SummaryView /> : <DetailView />}
    </div>
  );
};

export default SurveyResponses;
