import React, { useState } from 'react';
import { Card, Button, Accordion, Pagination } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const SurveyResponses = ({ surveys, onBack }) => {
  const [expandedResponse, setExpandedResponse] = useState(null);
  const [view, setView] = useState('summary'); // 'summary' or 'detail'
  const [currentSurveyPage, setCurrentSurveyPage] = useState(1); // Página actual de encuestas
  const [currentResponsePage, setCurrentResponsePage] = useState(1); // Página actual de respuestas
  const [responsesPerPage] = useState(5); // Número de respuestas por página
  const [surveysPerPage] = useState(5); // Número de encuestas por página

  // Agrupa las respuestas por fecha
  const groupedResponses = surveys.reduce((groups, survey) => {
    const date = new Date(survey._submission_time).toLocaleDateString();
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(survey);
    return groups;
  }, {});

  // Función mejorada para renderizar valores
  const renderValue = (value) => {
    if (value === null || value === undefined) {
      return 'No respondido';
    }
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }
    if (Array.isArray(value)) {
      return value.join(', ');
    }
    return String(value);
  };

  // Paginación de encuestas
  const indexOfLastSurvey = currentSurveyPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  const handleSurveyPageChange = (pageNumber) => {
    setCurrentSurveyPage(pageNumber);
  };

  // Paginación de respuestas dentro de cada encuesta
  const currentResponses = groupedResponses[expandedResponse]?.slice(
    (currentResponsePage - 1) * responsesPerPage,
    currentResponsePage * responsesPerPage
  );

  const handleResponsePageChange = (pageNumber) => {
    setCurrentResponsePage(pageNumber);
  };

  const renderSummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de Respuestas</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {Object.entries(groupedResponses).map(([date, responses]) => (
            <div key={date} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{date}</h6>
                  <small className="text-muted">
                    {responses.length} respuesta{responses.length !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setExpandedResponse(date);
                  }}
                >
                  Ver respuestas
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card.Body>

      {/* Paginación de encuestas */}
      <Pagination className="mt-4 justify-content-center">
        {[...Array(Math.ceil(surveys.length / surveysPerPage))].map((_, index) => (
          <Pagination.Item 
            key={index + 1} 
            active={index + 1 === currentSurveyPage} 
            onClick={() => handleSurveyPageChange(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </Card>
  );

  const renderDetailView = () => (
    <div>
      <Button 
        variant="link" 
        className="text-decoration-none mb-3"
        onClick={() => {
          setView('summary');
          setExpandedResponse(null);
        }}
      >
        <ArrowLeft /> Volver al resumen
      </Button>

      <div className="mb-3">
        <h5>
          Respuestas del {expandedResponse}
          <span className="badge bg-primary ms-2">
            {groupedResponses[expandedResponse]?.length || 0} respuestas
          </span>
        </h5>
      </div>

      <Accordion>
        {currentResponses?.map((response, index) => (
          <Accordion.Item key={index} eventKey={index.toString()}>
            <Accordion.Header>
              <div className="d-flex justify-content-between align-items-center w-100 me-3">
                <span>Respuesta #{index + 1}</span>
                <small className="text-muted">
                  {new Date(response._submission_time).toLocaleTimeString()}
                </small>
              </div>
            </Accordion.Header>
            <Accordion.Body className="bg-light">
              <div className="response-details">
                {Object.entries(response)
                  .filter(([key]) => {
                    // Excluir campos técnicos
                    const excludedFields = [
                      'formhub/uuid', '_xform_id_string', '_uuid', '_attachments', 
                      '_status', '_geolocation', '_tags', '_notes', '_validation_status',
                      '_submitted_by', 'start', 'end', '__version__', 'meta/instanceID',
                      '_submission_time',
                    ];
                    return !excludedFields.includes(key);
                  })
                  .map(([key, value]) => (
                    <div key={key} className="mb-3 border-bottom pb-3">
                      <div className="fw-medium text-primary mb-2">
                        {/* Mejoramos la visualización del nombre del campo */}
                        {key.startsWith('_') ? key.slice(1) : key}
                      </div>
                      <div className="ps-3">
                        {renderValue(value)}
                      </div>
                    </div>
                  ))}
              </div>
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>

      {/* Paginación de respuestas */}
      <Pagination className="mt-4 justify-content-center">
        {[...Array(Math.ceil(groupedResponses[expandedResponse]?.length / responsesPerPage))].map((_, index) => (
          <Pagination.Item 
            key={index + 1} 
            active={index + 1 === currentResponsePage} 
            onClick={() => handleResponsePageChange(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );

  return (
    <div className="container py-4">
      <div className="mb-4">
        <Button 
          variant="link" 
          onClick={onBack}
          className="text-decoration-none p-0"
        >
          <ArrowLeft /> Volver a proyectos
        </Button>
        <h4 className="mt-3">Respuestas de la Encuesta</h4>
      </div>

      {view === 'summary' ? renderSummaryView() : renderDetailView()}
    </div>
  );
};

export default SurveyResponses;
