import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Card, Button, Accordion, Image, Alert, Spinner } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

// URL de la API
const API_URL = 'http://localhost:5000/api/surveys';

// Campos que se deben excluir de la visualización
const EXCLUDED_FIELDS = new Set([
  '_id', 'start', 'end', 'formhub/uuid', '_status', '_geolocation',
  '_tags', '_notes', '_validation_status', '_submitted_by', '_submission_time',
  '__version__', '_uuid', 'meta/instanceID', 'meta/rootUuid', 'meta/deprecatedID'
]);

// Grupos de preguntas según la estructura de KoboToolbox
const GROUPS = {
  "Información General": ["nombre_subestacion", "Ubicacion", "codigo_id", "fecha_reporte", "inspector_responsable"],
  "Transformadores": ["cantidad_transformadores", "tipo_transformador", "potencia_nominal", "tension_primaria", "tension_secundaria", "relacion_transformacion", "temperatura_operacion", "nivel_aceite", "foto_nivel_aceite"],
  "Celdas e Interruptores": ["cantidad_celdas", "tipo_interruptor", "corriente_nominal", "voltaje_operacion", "estado_barras", "foto_estado_barras"],
  "Protección y Seguridad": ["tipo_reles", "configuracion_disparo", "corriente_disparo", "tiempos_disparo", "estado_baterias", "foto_estado_baterias"],
  "Puesta a Tierra": ["resistencia_tierra", "estado_conexiones_tierra", "foto_estado_tierra"],
  "Enfriamiento y Comunicación": ["tipo_enfriamiento", "estado_ventiladores", "foto_estado_ventiladores", "tipo_comunicacion", "estado_comunicacion"],
  "Condiciones Ambientales": ["temperatura_ambiente", "humedad_relativa", "condiciones_climaticas", "foto_condiciones_climaticas"],
  "Observaciones y Acciones Correctivas": ["observaciones_generales", "acciones_correctivas", "urgencia_accion", "firma_inspector"]
};

// Normaliza las claves eliminando prefijos de grupos
const normalizeKeys = (data) => {
  let normalizedData = {};
  for (let key in data) {
    let newKey = key.replace(/^.*\//, ''); // Elimina los prefijos de grupo
    normalizedData[newKey] = data[key];
  }
  return normalizedData;
};

// Extrae las imágenes y las asocia a su pregunta
const mapAttachments = (response) => {
  let imageMap = {};
  if (response._attachments) {
    response._attachments.forEach(att => {
      if (att.question_xpath) {
        let key = att.question_xpath.replace(/^.*\//, ''); // Normaliza la clave de imagen
        imageMap[key] = att.download_url;
      }
    });
  }
  return imageMap;
};

const BackButton = ({ onClick, text }) => (
  <Button variant="link" onClick={onClick} className="text-decoration-none p-0">
    <ArrowLeft /> {text}
  </Button>
);

const renderValue = (key, value, imageMap) => {
  if (value == null) return 'No respondido';

  // Si es una imagen, mostrarla correctamente
  if (imageMap[key]) {
    return (
      <div className="mt-2">
        <Image src={imageMap[key]} alt={key} thumbnail style={{ maxWidth: '150px', maxHeight: '150px' }} />
        <br />
        <a href={imageMap[key]} target="_blank" rel="noopener noreferrer">Ver imagen en tamaño completo</a>
      </div>
    );
  }

  // Si es una alerta por temperatura de operación
  if (key === 'temperatura_operacion' && Number(value) > 100) {
    return <Alert variant="warning"><strong>⚠ Advertencia:</strong> La temperatura de operación es alta ({value}°C)</Alert>;
  }

  return Array.isArray(value) ? value.join(', ') : String(value);
};

const SurveyResponses = ({ onBack }) => {
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState('summary');
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    const fetchSurveys = async () => {
      try {
        const response = await fetch(API_URL);
        if (!response.ok) throw new Error('Error al obtener los datos');
        const data = await response.json();
        setSurveys(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSurveys();
  }, []);

  const groupedResponses = useMemo(() => {
    return surveys.reduce((acc, survey) => {
      const date = new Date(survey._submission_time).toLocaleDateString();
      (acc[date] = acc[date] || []).push(survey);
      return acc;
    }, {});
  }, [surveys]);

  if (loading) return <Spinner animation="border" />;
  if (error) return <Alert variant="danger">Error: {error}</Alert>;

  return (
    <div className="container py-4">
      <div className="mb-4">
        <BackButton onClick={onBack} text="Volver a reportes" />
        <h4 className="mt-3">Reporte Subestación</h4>
      </div>
      {view === 'summary' ? (
        <Card className="border-0 shadow-sm">
          <Card.Header className="bg-white d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Resumen de reportes</h5>
            <span className="badge bg-primary">{surveys.length} total</span>
          </Card.Header>
          <Card.Body>
            {Object.entries(groupedResponses).map(([date, responses]) => (
              <div key={date} className="list-group-item">
                <h6>{date} <small className="text-muted">({responses.length} reporte{responses.length !== 1 ? 's' : ''})</small></h6>
                <Button variant="outline-primary" size="sm" onClick={() => { setView('detail'); setSelectedDate(date); }}>Ver reporte</Button>
              </div>
            ))}
          </Card.Body>
        </Card>
      ) : (
        <div>
          <BackButton onClick={() => { setView('summary'); setSelectedDate(null); }} text="Volver al resumen" />
          {groupedResponses[selectedDate]?.map((response, index) => {
            const normalizedData = normalizeKeys(response);
            const imageMap = mapAttachments(response);

            return (
              <Card key={index} className="mb-4 border-0 shadow-sm">
                <Card.Header className="bg-primary text-white">
                  <h5 className="mb-0">Reporte #{index + 1} - {normalizedData.inspector_responsable || "Sin nombre"}</h5>
                </Card.Header>
                <Card.Body>
                  <Accordion>
                    {Object.entries(GROUPS).map(([groupName, fields], groupIndex) => {
                      const groupData = fields.filter(field => normalizedData[field] !== undefined);
                      if (groupData.length === 0) return null;

                      return (
                        <Accordion.Item key={groupIndex} eventKey={groupIndex.toString()}>
                          <Accordion.Header>{groupName}</Accordion.Header>
                          <Accordion.Body>
                            {groupData.map(field => (
                              <div key={field} className="mb-3 border-bottom pb-3">
                                <div className="fw-medium text-primary mb-2">{field.replace(/_/g, ' ')}</div>
                                <div>{renderValue(field, normalizedData[field], imageMap)}</div>
                              </div>
                            ))}
                          </Accordion.Body>
                        </Accordion.Item>
                      );
                    })}
                  </Accordion>
                </Card.Body>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SurveyResponses;
