import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const surveysPerPage = 5;

  if (!Array.isArray(surveys) || surveys.length === 0) {
    return (
      <div className="container mt-4">
        <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
          ← Volver a proyectos
        </button>
        <h2 className="mb-4">Encuestas del proyecto</h2>
        <p>No hay encuestas disponibles.</p>
      </div>
    );
  }

  // Paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Encuestas del proyecto</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                {/* Nombre de la encuesta */}
                <h3 className="card-title text-primary">
                  {survey.name || `Encuesta #${indexOfFirstSurvey + index + 1}`}
                </h3>

                {/* Preguntas de la encuesta desde summary.labels */}
                {survey.summary?.labels?.length > 0 ? (
                  <ul className="list-group mb-3">
                    {survey.summary.labels.map((question, qIndex) => (
                      <li key={qIndex} className="list-group-item">
                        {question}
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p className="text-muted">No hay preguntas disponibles.</p>
                )}

                {/* Botón para ver respuestas de la encuesta */}
                {survey.data ? (
                  <a
                    href={survey.data}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-outline-primary"
                  >
                    Ver respuestas
                  </a>
                ) : (
                  <p className="text-danger">No hay datos de respuestas.</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <span className="align-self-center">
          Página {currentPage} de {Math.ceil(surveys.length / surveysPerPage)}
        </span>
        <Button
          onClick={nextPage}
          disabled={currentPage >= Math.ceil(surveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
