import React from "react";
import Header from "./Components/Header";
import Projects from "./Components/Projects";
import Footer from "./Components/Footer";
import LoadingSpinner from "./Components/Spinner"; // Importa el componente LoadingSpinner
import 'bootstrap/dist/css/bootstrap.min.css';
import './Style/Global.css';

function App() {
  const [isLoading, setIsLoading] = React.useState(true); // Estado para simular carga

  // Simula una carga con un timeout
  React.useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000); // Cambia el estado después de 3 segundos

    return () => clearTimeout(timer); // Limpia el timeout
  }, []);

  return (
    <div className="min-vh-100 d-flex flex-column">
      <Header />
      <main className="flex-grow-1 p-4">
        {isLoading ? <LoadingSpinner /> : <Projects />} {/* Muestra el spinner o el contenido */}
      </main>
      <Footer />
    </div>
  );
}

export default App;
