import React, { useState } from 'react';
import { Button, Card, Accordion, Pagination } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const surveysPerPage = 6;

  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);
  const totalPages = Math.ceil(surveys.length / surveysPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="container py-4">
      <div className="mb-4">
        <Button 
          variant="link" 
          onClick={onBack}
          className="d-flex align-items-center gap-2 text-muted text-decoration-none"
        >
          <ArrowLeft /> Volver a proyectos
        </Button>
      </div>

      <Card className="shadow-sm mb-4">
        <Card.Header>
          <h2 className="h4 mb-0">Encuestas del proyecto</h2>
        </Card.Header>
        <Card.Body>
          <div className="row g-4">
            {currentSurveys.map((survey, index) => (
              <div key={index} className="col-md-6">
                <Card className="h-100">
                  <Card.Header className="bg-light">
                    <h3 className="h5 mb-0">
                      Encuesta #{indexOfFirstSurvey + index + 1}
                    </h3>
                  </Card.Header>
                  <Card.Body>
                    <Accordion flush>
                      {Object.entries(survey).map(([key, value], i) => (
                        <Accordion.Item key={i} eventKey={`${index}-${i}`}>
                          <Accordion.Header>
                            <span className="fw-medium">{key}</span>
                          </Accordion.Header>
                          <Accordion.Body className="bg-light">
                            {typeof value === 'object' ? (
                              <pre className="mb-0" style={{ whiteSpace: 'pre-wrap' }}>
                                {JSON.stringify(value, null, 2)}
                              </pre>
                            ) : (
                              <span className="text-muted">{String(value)}</span>
                            )}
                          </Accordion.Body>
                        </Accordion.Item>
                      ))}
                    </Accordion>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </div>
        </Card.Body>
      </Card>

      <div className="d-flex justify-content-center">
        <Pagination>
          <Pagination.Prev
            onClick={() => handlePageChange(Math.max(1, currentPage - 1))}
            disabled={currentPage === 1}
          />
          
          {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
            <Pagination.Item
              key={page}
              active={currentPage === page}
              onClick={() => handlePageChange(page)}
            >
              {page}
            </Pagination.Item>
          ))}
          
          <Pagination.Next
            onClick={() => handlePageChange(Math.min(totalPages, currentPage + 1))}
            disabled={currentPage === totalPages}
          />
        </Pagination>
      </div>
    </div>
  );
};

export default SurveyList;
