import React, { useState, useMemo } from 'react';
import { Card, Button, Accordion } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

// Campos que no corresponden a preguntas
const EXCLUDED_FIELDS = new Set([
  'id',
  'formhub/uuid',
  '_xform_id_string',
  '_uuid',
  '_attachments',
  '_status',
  '_geolocation',
  '_tags',
  '_notes',
  '_validation_status',
  '_submitted_by',
  'start',
  'end',
  '__version__',
  'meta/instanceID',
  '_submission_time',
  // Puedes agregar aquí otros campos que no deseas mostrar
]);

// Función para normalizar cadenas: quita espacios, guiones y caracteres no alfanuméricos, y pasa a minúsculas.
const normalize = (str) =>
  str.replace(/[\s\W]/g, '').toLowerCase();

const BackButton = ({ onClick, text }) => (
  <Button variant="link" onClick={onClick} className="text-decoration-none p-0">
    <ArrowLeft /> {text}
  </Button>
);

const SurveyResponses = ({ surveys, summary, onBack }) => {
  const [view, setView] = useState('summary');
  const [selectedDate, setSelectedDate] = useState(null);

  // Asegurarse de que summary.labels existe (si no, usar arreglo vacío)
  const labels = summary && Array.isArray(summary.labels) ? summary.labels : [];

  // Agrupar respuestas por fecha usando _submission_time
  const groupedResponses = useMemo(() => {
    return surveys.reduce((acc, survey) => {
      const date = new Date(survey._submission_time).toLocaleDateString();
      (acc[date] = acc[date] || []).push(survey);
      return acc;
    }, {});
  }, [surveys]);

  // Renderiza el valor de la respuesta
  const renderValue = (value) => {
    if (value == null) return 'No respondido';
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }
    return Array.isArray(value) ? value.join(', ') : String(value);
  };

  const SummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de Respuestas</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {Object.entries(groupedResponses).map(([date, responses]) => (
            <div key={date} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{date}</h6>
                  <small className="text-muted">
                    {responses.length} respuesta{responses.length !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setSelectedDate(date);
                  }}
                >
                  Ver respuestas
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card.Body>
    </Card>
  );

  // Vista detalle: para cada submission, se itera sobre el array de etiquetas de summary.labels
  // y se busca en el submission la clave que corresponda (usando la función normalize)
  const DetailView = () => (
    <div>
      <BackButton
        onClick={() => {
          setView('summary');
          setSelectedDate(null);
        }}
        text="Volver al resumen"
      />
      <div className="mb-3">
        <h5 className="d-flex align-items-center gap-2">
          Respuestas del {selectedDate}
          <span className="badge bg-primary">
            {groupedResponses[selectedDate]?.length || 0} respuestas
          </span>
        </h5>
      </div>
      <Accordion>
        {groupedResponses[selectedDate]?.map((response, respIndex) => (
          <Accordion.Item key={respIndex} eventKey={respIndex.toString()}>
            <Accordion.Header>
              <div className="d-flex justify-content-between align-items-center w-100 me-3">
                <span>Respuesta #{respIndex + 1}</span>
                <small className="text-muted">
                  {new Date(response._submission_time).toLocaleTimeString()}
                </small>
              </div>
            </Accordion.Header>
            <Accordion.Body className="bg-light">
              {labels.map((label, idx) => {
                const normalizedLabel = normalize(label);
                // Busca la clave en response cuyo nombre normalizado coincida
                const answerKey = Object.keys(response)
                  .filter((k) => !EXCLUDED_FIELDS.has(k))
                  .find((k) => normalize(k) === normalizedLabel);
                return (
                  <div key={idx} className="mb-3 border-bottom pb-3">
                    <div className="fw-medium text-primary mb-2">{label}</div>
                    <div className="ps-3">
                      {answerKey ? renderValue(response[answerKey]) : 'No respondido'}
                    </div>
                  </div>
                );
              })}
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
    </div>
  );

  return (
    <div className="container py-4">
      <div className="mb-4">
        <BackButton onClick={onBack} text="Volver a encuestas" />
        <h4 className="mt-3">Respuestas de la Encuesta</h4>
      </div>
      {view === 'summary' ? <SummaryView /> : <DetailView />}
    </div>
  );
};

export default SurveyResponses;
