import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [surveysPerPage] = useState(5); // Número de encuestas por página

  // Lógica de paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Función para limpiar el texto de las preguntas
  const cleanLabel = (label) => {
    return label.replace(/[_-]/g, ' '); // Reemplaza guiones y guiones bajos con espacios
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Preguntas de las encuestas</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">Encuesta #{index + 1}</h3>
                {/* Mostrar solo las preguntas */}
                {survey.summary?.labels ? (
                  survey.summary.labels.map((label, qIndex) => (
                    <div key={qIndex} className="mb-2">
                      <strong className="text-secondary">Pregunta {qIndex + 1}:</strong>{' '}
                      <span className="text-dark">{cleanLabel(label)}</span>
                    </div>
                  ))
                ) : (
                  <p className="text-muted">No hay preguntas en esta encuesta.</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <Button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(surveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
