import React, { useState, useMemo, useEffect, useRef } from 'react';
import { Card, Button, Accordion, Image, Alert } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import { fromLonLat } from 'ol/proj';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Feature from 'ol/Feature';
import Point from 'ol/geom/Point';
import Style from 'ol/style/Style';
import Icon from 'ol/style/Icon';

const EXCLUDED_FIELDS = new Set([
  '_id', 'formhub/uuid', '_xform_id_string', '_uuid', '_attachments',
  '_status', '_geolocation', '_tags', '_notes', '_validation_status',
  '_submitted_by', 'start', 'end', '__version__', 'meta/instanceID',
  '_submission_time'
]);

const BackButton = ({ onClick, text }) => (
  <Button 
    variant="link" 
    onClick={onClick}
    className="text-decoration-none p-0"
  >
    <ArrowLeft /> {text}
  </Button>
);

const MapComponent = ({ lat, lon }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);

  useEffect(() => {
    if (!mapRef.current) return;

    const coordinates = fromLonLat([lon, lat]);

    if (!mapInstance.current) {
      const marker = new Feature({
        geometry: new Point(coordinates),
      });

      marker.setStyle(new Style({
        image: new Icon({
          anchor: [0.5, 1],
          src: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
          scale: 0.05 // Reducido para que el marcador sea más pequeño
        })
      }));

      const vectorLayer = new VectorLayer({
        source: new VectorSource({
          features: [marker]
        })
      });

      mapInstance.current = new Map({
        target: mapRef.current,
        layers: [
          new TileLayer({
            source: new OSM()
          }),
          vectorLayer
        ],
        view: new View({
          center: coordinates,
          zoom: 15
        })
      });
    } else {
      mapInstance.current.getView().setCenter(coordinates);
    }
  }, [lat, lon]);

  return <div ref={mapRef} style={{ height: '150px', width: '100%', marginTop: '10px' }}></div>;
};

const renderValue = (key, value, response) => {
  if (value == null) return 'No respondido';
  if (typeof value === 'object' && !Array.isArray(value)) {
    return (
      <pre className="mb-0 bg-white p-2 rounded">
        {JSON.stringify(value, null, 2)}
      </pre>
    );
  }

  if (key === 'temperatura_operacion' && Number(value) > 100) {
    return (
      <Alert variant="warning">
        <strong>⚠ Advertencia:</strong> La temperatura de operación es alta ({value}°C)
      </Alert>
    );
  }

  return Array.isArray(value) ? value.join(', ') : String(value);
};

export default SurveyResponses;
