import { useState, useEffect } from 'react';
import axios from 'axios';
import { Accordion, Button, Card } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const SurveyResponses = ({ surveys, onBack }) => {
  const [expandedResponse, setExpandedResponse] = useState(null);
  const [view, setView] = useState('summary'); // 'summary' or 'detail'
  const [questions, setQuestions] = useState([]); // Almacenar preguntas de la API

  // Cargar preguntas al inicio
  useEffect(() => {
    // Realizar un segundo llamado a la API para obtener las preguntas
    const fetchQuestions = async () => {
      try {
        const surveyUrl = surveys[0]?.data; // Utiliza la URL de data de la primera encuesta (puedes personalizar esto según lo que necesites)
        if (surveyUrl) {
          const response = await axios.get(surveyUrl);
          setQuestions(response.data); // Guarda las preguntas
        }
      } catch (error) {
        console.error('Error al obtener preguntas:', error);
      }
    };

    fetchQuestions();
  }, [surveys]);

  // Agrupa las respuestas por fecha
  const groupedResponses = surveys.reduce((groups, survey) => {
    const date = new Date(survey._submission_time).toLocaleDateString();
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(survey);
    return groups;
  }, {});

  // Función para mostrar valores de respuesta
  const renderValue = (value) => {
    if (value === null || value === undefined) {
      return 'No respondido';
    }
    if (typeof value === 'object' && !Array.isArray(value)) {
      return (
        <pre className="mb-0 bg-white p-2 rounded">
          {JSON.stringify(value, null, 2)}
        </pre>
      );
    }
    if (Array.isArray(value)) {
      return value.join(', ');
    }
    return String(value);
  };

  const renderSummaryView = () => (
    <Card className="border-0 shadow-sm">
      <Card.Header className="bg-white border-bottom-0 d-flex justify-content-between align-items-center">
        <h5 className="mb-0">Resumen de Respuestas</h5>
        <span className="badge bg-primary">{surveys.length} total</span>
      </Card.Header>
      <Card.Body className="p-0">
        <div className="list-group list-group-flush">
          {Object.entries(groupedResponses).map(([date, responses]) => (
            <div key={date} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center py-2">
                <div>
                  <h6 className="mb-0">{date}</h6>
                  <small className="text-muted">
                    {responses.length} respuesta{responses.length !== 1 ? 's' : ''}
                  </small>
                </div>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => {
                    setView('detail');
                    setExpandedResponse(date);
                  }}
                >
                  Ver respuestas
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card.Body>
    </Card>
  );

  const renderDetailView = () => (
    <div>
      <Button 
        variant="link" 
        className="text-decoration-none mb-3"
        onClick={() => {
          setView('summary');
          setExpandedResponse(null);
        }}
      >
        <ArrowLeft /> Volver al resumen
      </Button>

      <div className="mb-3">
        <h5>
          Respuestas del {expandedResponse}
          <span className="badge bg-primary ms-2">
            {groupedResponses[expandedResponse]?.length || 0} respuestas
          </span>
        </h5>
      </div>

      <Accordion>
        {groupedResponses[expandedResponse]?.map((response, index) => {
          console.log('Respuesta:', response); // Inspecciona los datos de la respuesta

          return (
            <Accordion.Item key={index} eventKey={index.toString()}>
              <Accordion.Header>
                <div className="d-flex justify-content-between align-items-center w-100 me-3">
                  <span>Respuesta #{index + 1}</span>
                  <small className="text-muted">
                    {new Date(response._submission_time).toLocaleTimeString()}
                  </small>
                </div>
              </Accordion.Header>
              <Accordion.Body className="bg-light">
                <div className="response-details">
                  {questions.map((question, qIndex) => (
                    <div key={qIndex} className="mb-3 border-bottom pb-3">
                      <div className="fw-medium text-primary mb-2">
                        {question.label}  {/* Mostrar la pregunta */}
                      </div>
                      <div className="ps-3">
                        {renderValue(response[question.name])}  {/* Mostrar la respuesta */}
                      </div>
                    </div>
                  ))}
                </div>
              </Accordion.Body>
            </Accordion.Item>
          );
        })}
      </Accordion>
    </div>
  );

  return (
    <div className="container py-4">
      <div className="mb-4">
        <Button 
          variant="link" 
          onClick={onBack}
          className="text-decoration-none p-0"
        >
          <ArrowLeft /> Volver a proyectos
        </Button>
        <h4 className="mt-3">Respuestas de la Encuesta</h4>
      </div>

      {view === 'summary' ? renderSummaryView() : renderDetailView()}
    </div>
  );
};

export default SurveyResponses;
