import React, { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import SurveyList from './SurveyList';
import Header from './Header'; // Importa el Header
import Footer from './Footer'; // Importa el Footer
import 'bootstrap/dist/css/bootstrap.min.css'; // Importa Bootstrap CSS

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar proyectos
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/projects');
      if (!response.ok) {
        throw new Error('Error al obtener los proyectos');
      }
      const data = await response.json();

      // Filtra proyectos que tengan nombre y descripción
      const filteredProjects = data.filter(
        (project) => project.name && project.settings?.description
      );

      setProjects(filteredProjects);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Cargar encuestas de un proyecto
  const fetchSurveys = async (projectId) => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/projects/${projectId}/responses`);
      if (!response.ok) {
        throw new Error('Error al obtener las encuestas');
      }
      const data = await response.json();
      setSurveys(data);
      setSelectedProject(projectId);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="text-center mt-4">Cargando...</div>;
  }

  if (error) {
    return (
      <div className="container mt-4">
        <p className="text-danger">Error: {error}</p>
        <button className="btn btn-primary" onClick={fetchProjects}>
          Reintentar
        </button>
      </div>
    );
  }

  return (
    <div>
      <Header /> {/* Agrega el Header */}
      <div className="container mt-4">
        <h1>Proyectos de KoboToolbox</h1>

        {!selectedProject ? (
          <ProjectList projects={projects} onProjectSelect={fetchSurveys} />
        ) : (
          <SurveyList
            surveys={surveys}
            onBack={() => setSelectedProject(null)}
          />
        )}
      </div>
      <Footer /> {/* Agrega el Footer */}
    </div>
  );
};

export default Projects;
