import React, { useState } from 'react';
import { Button } from 'react-bootstrap'; // Importa el componente Button de react-bootstrap

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [surveysPerPage] = useState(5); // Número de encuestas por página

  // Lógica de paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  // Mapeo de preguntas a claves de respuestas
  const questionKeyMap = {
    "Por favor, ingrese su nombre.": "Por_favor_ingrese_su_nombre",
    "¿A qué empresa u organización pertenece?": "_A_qu_empresa_u_organizaci_n_pertenece",
    "¿En qué industria se desempeña su empresa?": "_En_qu_industria_se_desempe_a_su_empresa",
    "¿Qué servicios de Conecta ha utilizado?": "_Qu_servicios_de_Conecta_ha_utilizado",
    "En una escala del 1 al 5, ¿cómo calificaría su satisfacción general con los servicios de Conecta?": "En_una_escala_del_1_servicios_de_Conecta"
  };

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Encuestas del proyecto</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">Encuesta #{index + 1}</h3>
                {survey.summary?.labels?.map((question, questionIndex) => {
                  // Obtener la clave de la respuesta usando el mapeo
                  const questionKey = questionKeyMap[question];

                  // Mostrar la pregunta y la respuesta
                  return (
                    <div key={questionIndex} className="mb-2">
                      <strong className="text-secondary">Pregunta {questionIndex + 1}:</strong>{' '}
                      <span className="text-dark">{question}</span>
                      <br />
                      <strong className="text-secondary">Respuesta:</strong>{' '}
                      <span className="text-dark">{survey[questionKey] || "Sin respuesta"}</span>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <Button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(surveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
