import { useState } from "react";
import { FaChevronDown } from "react-icons/fa";
import { Link } from "react-router-dom";

const Header = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen((prev) => !prev);
  };

  return (
    <header className="bg-green-600 text-white py-4 px-8 flex justify-between items-center shadow-md">
      {/* Logo */}
      <div className="flex items-center">
        <span className="bg-white text-green-600 font-bold px-2 py-1 text-lg rounded-md">CONECTA</span>
        <span className="ml-2 text-sm">Electrical Automation Solutions</span>
      </div>

      {/* Nav Menu */}
      <nav className="flex space-x-6">
        <Link to="/" className="hover:underline transition duration-300 ease-in-out hover:text-green-200">Home</Link>
        <Link to="/nuestra-compania" className="hover:underline transition duration-300 ease-in-out hover:text-green-200">Nuestra Compañía</Link>

        {/* Dropdown */}
        <div className="relative">
          <button
            onClick={toggleDropdown}
            className="flex items-center hover:underline transition duration-300 ease-in-out hover:text-green-200"
            aria-haspopup="true"
            aria-expanded={isDropdownOpen}
          >
            Soluciones Conecta <FaChevronDown className="ml-1 text-xs" />
          </button>
          {isDropdownOpen && (
            <div className="absolute mt-2 bg-white text-green-600 shadow-md rounded-md w-48">
              <Link to="/solucion1" className="block px-4 py-2 hover:bg-green-100">Opción 1</Link>
              <Link to="/solucion2" className="block px-4 py-2 hover:bg-green-100">Opción 2</Link>
            </div>
          )}
        </div>

        <Link to="/blog" className="hover:underline transition duration-300 ease-in-out hover:text-green-200">Blog</Link>
        <Link to="/contacto" className="hover:underline transition duration-300 ease-in-out hover:text-green-200">Contacto</Link>
      </nav>
    </header>
  );
};

export default Header;
