import React, { useState } from 'react';
import { Button } from 'react-bootstrap'; // Importa el componente Button de react-bootstrap

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const surveysPerPage = 5; // Número de encuestas por página

  // Verificamos que surveys tenga datos antes de realizar operaciones
  if (!Array.isArray(surveys) || surveys.length === 0) {
    return (
      <div className="container mt-4">
        <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
          ← Volver a proyectos
        </button>
        <h2 className="mb-4">Encuestas del proyecto</h2>
        <p>No hay encuestas disponibles.</p>
      </div>
    );
  }

  // Lógica de paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Encuestas del proyecto</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">
                  Encuesta #{indexOfFirstSurvey + index + 1}
                </h3>
                {survey && typeof survey === 'object' ? (
                  Object.entries(survey).map(([key, value]) => (
                    <div key={key} className="mb-2">
                      <strong className="text-secondary">{key}:</strong>{' '}
                      <span className="text-dark">{String(value)}</span>
                    </div>
                  ))
                ) : (
                  <p className="text-danger">Datos no válidos para esta encuesta.</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <span className="align-self-center">
          Página {currentPage} de {Math.ceil(surveys.length / surveysPerPage)}
        </span>
        <Button
          onClick={nextPage}
          disabled={currentPage >= Math.ceil(surveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
