import React, { useState } from "react";
import { Button } from "react-bootstrap"; // Importa el componente Button de react-bootstrap

const SurveyList = ({ surveys, onBack }) => {
  const [currentPage, setCurrentPage] = useState(1); // Estado para la página actual
  const [surveysPerPage] = useState(5); // Número de encuestas por página

  // Lógica de paginación
  const indexOfLastSurvey = currentPage * surveysPerPage;
  const indexOfFirstSurvey = indexOfLastSurvey - surveysPerPage;
  const currentSurveys = surveys.slice(indexOfFirstSurvey, indexOfLastSurvey);

  // Cambiar de página
  const nextPage = () => {
    if (currentPage < Math.ceil(surveys.length / surveysPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-outline-secondary mb-4" onClick={onBack}>
        ← Volver a proyectos
      </button>
      <h2 className="mb-4">Encuestas del proyecto</h2>
      <div className="row">
        {currentSurveys.map((survey, index) => (
          <div key={survey.uid} className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h3 className="card-title text-primary">{survey.name || "Sin nombre"}</h3>

                {/* Preguntas del formulario (validación de undefined) */}
                <div className="mb-2">
                  <strong className="text-secondary">Preguntas:</strong>{" "}
                  {survey.summary && survey.summary.labels
                    ? survey.summary.labels.join(", ")
                    : "No hay preguntas"}
                </div>

                {/* Estado de despliegue */}
                <div className="mb-2">
                  <strong className="text-secondary">Estado:</strong>{" "}
                  <span className="text-dark">{survey.deployment_status || "Desconocido"}</span>
                </div>

                {/* Archivos disponibles para descargar */}
                <div className="mb-2">
                  <strong className="text-secondary">Descargas:</strong>{" "}
                  {survey.downloads && survey.downloads.length > 0 ? (
                    survey.downloads.map((file) => (
                      <a
                        key={file.format}
                        href={file.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="d-block text-dark"
                      >
                        {file.format.toUpperCase()}
                      </a>
                    ))
                  ) : (
                    <span>No hay archivos disponibles</span>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Botones de paginación */}
      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </Button>
        <Button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(surveys.length / surveysPerPage)}
        >
          Siguiente
        </Button>
      </div>
    </div>
  );
};

export default SurveyList;
