import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Importa Bootstrap

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [surveys, setSurveys] = useState([]);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Mapeo de nombres de campos a nombres legibles
  const fieldNameMapping = {
    "Por_favor_ingrese_su_nombre": "Nombre",
    "_A_qu_empresa_u_organizaci_n_pertenece": "Empresa/Organización",
    "_En_qu_industria_se_desempe_a_su_empresa": "Industria",
    "_Qu_servicios_de_Conecta_ha_utilizado": "Servicios utilizados",
    "En_una_escala_del_1_servicios_de_Conecta": "Satisfacción general (1-5)",
    "En_una_escala_del_1_rcionado_por_Conecta": "Calificación del servicio (1-5)",
    "_Tiene_alg_n_comenta_icional_para_Conecta": "Comentarios adicionales",
    "_submission_time": "Fecha de envío",
    "_geolocation": "Ubicación geográfica",
  };

  // Cargar proyectos
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/projects');
      if (!response.ok) {
        throw new Error('Error al obtener los proyectos');
      }
      const data = await response.json();

      // Filtra proyectos que tengan nombre y descripción
      const filteredProjects = data.filter(
        (project) => project.name && project.settings?.description
      );

      setProjects(filteredProjects);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Cargar encuestas y reportes de un proyecto
  const fetchProjectData = async (projectId) => {
    try {
      setLoading(true);

      // Obtener encuestas
      const surveysResponse = await fetch(
        `http://localhost:5000/api/projects/${projectId}/responses`
      );
      if (!surveysResponse.ok) {
        throw new Error('Error al obtener las encuestas');
      }
      const surveysData = await surveysResponse.json();
      setSurveys(surveysData);

      // Obtener reportes
      const reportsResponse = await fetch(
        `https://kf.kobotoolbox.org/api/v2/assets/${projectId}/reports/`
      );
      if (!reportsResponse.ok) {
        throw new Error('Error al obtener los reportes');
      }
      const reportsData = await reportsResponse.json();
      setReports(reportsData);

      setSelectedProject(projectId);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Cargando...</div>;
  }

  if (error) {
    return (
      <div>
        <p>Error: {error}</p>
        <button onClick={fetchProjects}>Reintentar</button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1>Proyectos de KoboToolbox</h1>

      {!selectedProject ? (
        // Lista de proyectos
        <div>
          <h2>Selecciona un proyecto:</h2>
          {projects.map((project) => (
            <div key={project.uid} className="card mb-3">
              <div className="card-body">
                <h3 className="card-title">{project.name}</h3>
                <p className="card-text">
                  Descripción: {project.settings?.description || 'Sin descripción'}
                </p>
                <button
                  className="btn btn-primary"
                  onClick={() => fetchProjectData(project.uid)}
                >
                  Ver encuestas y reportes
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        // Vista de encuestas y reportes
        <div>
          <button
            className="btn btn-secondary mb-3"
            onClick={() => setSelectedProject(null)}
          >
            ← Volver a proyectos
          </button>
          <h2>Encuestas y Reportes del proyecto</h2>

          {/* Pestañas para encuestas y reportes */}
          <ul className="nav nav-tabs mb-3">
            <li className="nav-item">
              <button
                className="nav-link active"
                data-bs-toggle="tab"
                data-bs-target="#encuestas"
              >
                Encuestas
              </button>
            </li>
            <li className="nav-item">
              <button
                className="nav-link"
                data-bs-toggle="tab"
                data-bs-target="#reportes"
              >
                Reportes
              </button>
            </li>
          </ul>

          {/* Contenido de las pestañas */}
          <div className="tab-content">
            <div className="tab-pane fade show active" id="encuestas">
              {surveys.map((survey, index) => (
                <div key={index} className="card mb-3">
                  <div className="card-body">
                    <h3 className="card-title">Encuesta #{index + 1}</h3>
                    {Object.entries(survey).map(([key, value]) => {
                      // Excluir campos innecesarios
                      if (
                        key.startsWith('_') &&
                        !['_submission_time', '_geolocation'].includes(key)
                      ) {
                        return null;
                      }

                      // Usar el mapeo para nombres legibles
                      const displayName = fieldNameMapping[key] || key;

                      return (
                        <div key={key} className="mb-2">
                          <strong>{displayName}:</strong> {String(value)}
                        </div>
                      );
                    })}
                  </div>
                </div>
              ))}
            </div>

            <div className="tab-pane fade" id="reportes">
              {reports.length > 0 ? (
                reports.map((report, index) => (
                  <div key={index} className="card mb-3">
                    <div className="card-body">
                      <h3 className="card-title">Reporte #{index + 1}</h3>
                      <pre>{JSON.stringify(report, null, 2)}</pre>
                    </div>
                  </div>
                ))
              ) : (
                <p>No hay reportes disponibles para este proyecto.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Projects;
