import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Card, Button, Accordion, Image, Alert, Spinner } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import { fromLonLat } from 'ol/proj';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Feature from 'ol/Feature';
import Point from 'ol/geom/Point';
import Style from 'ol/style/Style';
import Icon from 'ol/style/Icon';

// URL de la API
const API_BASE_URL = 'http://localhost:5000/api';

// Función para obtener respuestas de KoboToolbox
const fetchSurveys = async (projectId) => {
  const response = await fetch(`${API_BASE_URL}/projects/${projectId}/responses`);
  if (!response.ok) {
    throw new Error('Error al obtener los reportes');
  }
  return response.json();
};

// Campos a excluir
const EXCLUDED_FIELDS = new Set([
  '_id', 'start', 'end', 'formhub/uuid', '_status', '_geolocation',
  '_tags', '_notes', '_validation_status', '_submitted_by', '_submission_time',
  '__version__', '_uuid', 'meta/instanceID', 'meta/rootUuid', 'meta/deprecatedID'
]);

// Normaliza claves eliminando prefijos `group_xxx/`
const normalizeKeys = (data) => {
  let normalizedData = {};
  for (let key in data) {
    let newKey = key.split('/').pop(); // Elimina prefijos de grupo
    normalizedData[newKey] = data[key];
  }
  return normalizedData;
};

// Asocia imágenes con sus preguntas correspondientes
const mapAttachments = (response) => {
  let imageMap = {};
  if (response._attachments) {
    response._attachments.forEach(att => {
      let key = att.question_xpath ? att.question_xpath.split('/').pop() : null;
      if (key) imageMap[key] = att.download_url;
    });
  }
  return imageMap;
};

// Extrae coordenadas de ubicación correctamente
const parseCoordinates = (locationString) => {
  if (!locationString) return null;
  let coords = locationString.split(' ');
  if (coords.length >= 2) {
    return { lat: parseFloat(coords[0]), lon: parseFloat(coords[1]) };
  }
  return null;
};

const BackButton = ({ onClick, text }) => (
  <Button variant="link" onClick={onClick} className="text-decoration-none p-0">
    <ArrowLeft /> {text}
  </Button>
);

const MapComponent = ({ lat, lon }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);

  useEffect(() => {
    if (!mapRef.current) return;

    const coordinates = fromLonLat([lon, lat]);

    if (!mapInstance.current) {
      const marker = new Feature({ geometry: new Point(coordinates) });
      marker.setStyle(new Style({
        image: new Icon({
          anchor: [0.5, 1],
          src: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
          scale: 0.03
        })
      }));

      const vectorLayer = new VectorLayer({
        source: new VectorSource({ features: [marker] })
      });

      mapInstance.current = new Map({
        target: mapRef.current,
        layers: [
          new TileLayer({ source: new OSM() }),
          vectorLayer
        ],
        view: new View({ center: coordinates, zoom: 15 })
      });
    } else {
      mapInstance.current.getView().setCenter(coordinates);
    }
  }, [lat, lon]);

  return <div ref={mapRef} style={{ height: '200px', width: '100%', marginTop: '10px' }}></div>;
};

const renderValue = (key, value, imageMap) => {
  if (value == null) return 'No respondido';

  // Si es una imagen
  if (imageMap[key]) {
    return (
      <div className="mt-2">
        <Image src={imageMap[key]} alt={key} thumbnail style={{ maxWidth: '150px', maxHeight: '150px' }} />
        <br />
        <a href={imageMap[key]} target="_blank" rel="noopener noreferrer">Ver imagen en tamaño completo</a>
      </div>
    );
  }

  // Si es una alerta (ej: temperatura alta)
  if (key === 'temperatura_operacion' && Number(value) > 100) {
    return <Alert variant="warning"><strong>⚠ Advertencia:</strong> Temperatura alta ({value}°C)</Alert>;
  }

  return Array.isArray(value) ? value.join(', ') : String(value);
};

const SurveyResponses = ({ projectId, onBack }) => {
  const [surveys, setSurveys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState('summary');
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    const loadSurveys = async () => {
      try {
        const data = await fetchSurveys(projectId);
        setSurveys(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    loadSurveys();
  }, [projectId]);

  const groupedResponses = useMemo(() => {
    return surveys.reduce((acc, survey) => {
      const date = new Date(survey._submission_time).toLocaleDateString();
      (acc[date] = acc[date] || []).push(survey);
      return acc;
    }, {});
  }, [surveys]);

  if (loading) return <Spinner animation="border" />;
  if (error) return <Alert variant="danger">Error: {error}</Alert>;

  return (
    <div className="container py-4">
      <div className="mb-4">
        <BackButton onClick={onBack} text="Volver a reportes" />
        <h4 className="mt-3">Reporte Subestación</h4>
      </div>
      {view === 'summary' ? (
        <Card className="border-0 shadow-sm">
          <Card.Header className="bg-white d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Resumen de reportes</h5>
            <span className="badge bg-primary">{surveys.length} total</span>
          </Card.Header>
          <Card.Body>
            {Object.entries(groupedResponses).map(([date, responses]) => (
              <div key={date} className="list-group-item">
                <h6>{date} <small className="text-muted">({responses.length} reporte{responses.length !== 1 ? 's' : ''})</small></h6>
                <Button variant="outline-primary" size="sm" onClick={() => { setView('detail'); setSelectedDate(date); }}>Ver reporte</Button>
              </div>
            ))}
          </Card.Body>
        </Card>
      ) : (
        <div>
          <BackButton onClick={() => { setView('summary'); setSelectedDate(null); }} text="Volver al resumen" />
          {groupedResponses[selectedDate]?.map((response, index) => {
            const normalizedData = normalizeKeys(response);
            const imageMap = mapAttachments(response);

            return (
              <Accordion key={index} className="mb-4">
                {Object.entries(normalizedData).map(([key, value]) => (
                  <div key={key} className="mb-3 border-bottom pb-3">
                    <strong>{key.replace(/_/g, ' ')}:</strong> {renderValue(key, value, imageMap)}
                  </div>
                ))}
              </Accordion>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SurveyResponses;
